import cv2import numpy as npimport pyautoguicap = cv2.VideoCapture(0)low_red = np.array([161, 155, 84])high_red = np.array([179, 255, 255])pre_y = 0while True:    ret, frame = cap.read()    hsv = cv2.cvtColor(frame, cv2.COLOR_BGR2HSV)    mask = cv2.inRange(hsv, low_red, high_red)    contours, hierarchy = cv2.findContours(mask, cv2.RETR_EXTERNAL, cv2.CHAIN_APPROX_SIMPLE)    # cv2.drawContours(frame, contours, -1, (255, 255, 255), 2)    for contour in contours:        area = cv2.contourArea(contour)        if area > 300:            # cv2.drawContours(frame, contour, -1, (255, 255, 255), 2)            x, y, h, w = cv2.boundingRect(contour)            cv2.rectangle(frame, (x, y), (x + w, y + h), (255, 255, 255), 3)            if y < pre_y:                pyautogui.press('space')            pre_y = y            # print(area)    # cv2.imshow('mask', mask)    cv2.imshow('frame', frame)    if cv2.waitKey(1) & 0xFF == ord('q'):        break# When everything done, release the capturecap.release()cv2.destroyAllWindows()