import cv2import numpy as npcap = cv2.VideoCapture(0)lower_black = np.array([0, 0, 0], dtype="uint16")upper_black = np.array([70, 70, 70], dtype="uint16")while True:    ret, frame = cap.read()    hsv = cv2.cvtColor(frame, cv2.COLOR_BGR2HSV)    mask = cv2.inRange(hsv, lower_black, upper_black)    contours, hierarchy = cv2.findContours(mask, cv2.RETR_EXTERNAL, cv2.CHAIN_APPROX_SIMPLE)    # cv2.drawContours(frame, contours, -1, (255, 255, 255), 2)    for contour in contours:        area = cv2.contourArea(contour)        if area > 300:            # cv2.drawContours(frame, contour, -1, (255, 255, 255), 2)            x, y, h, w = cv2.boundingRect(contour)            cv2.rectangle(frame, (x, y), (x + w, y + h), (255, 255, 255), 3)            print(area)    # cv2.imshow('mask', mask)    cv2.imshow('frame', frame)    if cv2.waitKey(1) & 0xFF == ord('q'):        break# When everything done, release the capturecap.release()cv2.destroyAllWindows()